import { createSlice } from "@reduxjs/toolkit";

const initialValue = {
  value: 0,
  products: [],
};

const cartSlice = createSlice({
  name: "cart",
  initialValue,
  reducers: {
    increment: (state) => {
      state.value += 1;
    },

    decrement: (state) => {
      state.value -= 1;
    },

    addProduct: (state) => {
      state.products.push(action.product);
    },
  },
});

export const cartActions = cartSlice.actions;
export default cartSlice.reducer;
